import angular from 'angular';
import Dialog from '../ng-dialog';

angular.module('demo', [ 'ngui' ])
  .controller('rootCtrl', ['ngDialog', (ngDialog) => {
    ngDialog.show({
      templateUrl: '/test/modal.html',
      parentElement: document.querySelector('#main'),
      controller: function() {
        this.title = 'hello';
        this.content = 'world';
        this.onConfirm = function() {
          alert('hi world'); 
        };
      },
      controllerAs: 'modal'
    });
  }]);
